import React, { useState } from "react";

function App() {
  let time = new Date().toLocaleTimeString();
  console.log(time);
  let [updatedTime, updateTime] = useState(time);

  function UpdateTime() {
    updateTime(updatedTime);
  }
  return (
    <div className="container">
      <h1>{updatedTime}</h1>
      <button onClick={UpdateTime}>Get Time</button>
    </div>
  );
}

export default App;
